import React from "react";

class FieldGenerator extends React.Component { 
    constructor(props) {
        super(props);
        this.state = {
            addPercentField: '',
            addDollarField: ''
        }
      }

    onPercentCreate = () => {
    this.setState({addPercentField: this.state.addPercentField});
    }

    onDollarCreate = () => {
    this.setState({addDollarField: this.state.addDollarField});
    }

    render() {
    return (
        <div className="tc">
            <p>What type of pricing do you use?</p>
            <button
            className=""
            onClick={this.onPercentCreate}
            >Percentage (%)</button>
            <button
            className=""
            onClick={this.onDollarCreate}
            >Dollar ($)</button>
        </div>
    )
}
}

export default FieldGenerator;
