import React from "react";
import SavingsResult from "./SavingsResult";
import MatchResult from "./MatchResult";

class PercentCalculator extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            customerRate: 0,
        }
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange (e) {
        this.setState({ [e.target.name]: e.target.value });
        e.preventDefault();
        console.log(this.state.customerRate)
    }

    render() {
        const partnerWizard = this.props.percentCalculator[this.props.calcId];
        const promptText = partnerWizard.prompt;
        const yourRate = partnerWizard.rate;
        if (this.state.customerRate === 0) {
            return (
                <div className="ba br2 mb3">
                    <form onSubmit={this.handleChange}>
                        <h2>{promptText}</h2>
                            <p><input
                                onChange={this.handleChange} 
                                className="br2" 
                                type="text"
                                name="customerRate"
                                placeholder="4"/>
                                %</p>
                            <button
                                className="f6 link dim br3 ph3 pv2 mb2 dib white bg-dark-blue"
                            >See your savings!</button>
                    </form>
                </div>
            );
            } else if (this.state.customerRate - yourRate > 0) {
                return (
                    <div className="ba br2 mb3">
                        <div>
                            <form onSubmit={this.handleChange}>
                                <h2>{promptText}</h2>
                                    <p><input
                                        onChange={this.handleChange} 
                                        className="br2" 
                                        type="text"
                                        name="customerRate"
                                        placeholder="4"/>
                                        %</p>
                                    <button
                                        className="f6 link dim br3 ph3 pv2 mb2 dib white bg-dark-blue"
                                    >See your savings!</button>
                            </form>
                        </div>
                        <div>
                            <SavingsResult
                            yourRate={this.yourRate}
                            customerRate={this.customerRate}
                            />
                        </div>
                    </div>
                );
            } else {
                return (
                    <div>
                        <div className="ba br2 mb3">
                            <form onSubmit={this.handleChange}>
                                <h2>{promptText}</h2>
                                    <p><input
                                        onChange={this.handleChange} 
                                        className="br2" 
                                        type="text"
                                        name="customerRate"
                                        placeholder="4"/>
                                        %</p>
                                    <button
                                        className="f6 link dim br3 ph3 pv2 mb2 dib white bg-dark-blue"
                                    >See your savings!</button>
                            </form>
                        </div>
                        <div>
                        <MatchResult />
                        </div>
                    </div>
                )
            }
        }
}

export default PercentCalculator;
