import React from "react";
import PerCalcHtml from "./PerCalcHtml";
import DolCalcHtml from "./DolCalcHtml";

class CodeOutput extends React.Component {

    render() {
        const perCalcHtmlRender = this.props.percentCalculators.map((key, count) =>
            <div
                className="ph2 pv1 mb2 mh4 ba br2 bg-white-40 b--white-30 shadow-4"
                id={key} 
                key={count}>
                    <PerCalcHtml
                    percentCalculator={this.props.percentCalculators}
                    calcId={count}
                    />
            </div>
        )
        const dolCalcHtmlRender = this.props.dollarCalculators.map((key, count) =>
            <div
                className="tc ph2 pv1 mb2 mh4 ba br2 bg-white-40 b--white-30 shadow-4"
                id={key}
                key={count}>
                    <DolCalcHtml
                    dollarCalculator={this.props.dollarCalculators}
                    calcId={count}
                    />
            </div>
        )

        return (
            <div className="tc mh2">
                <h2 className="f3 lh-copy fw4 white-90">4. Copy the code below to your website to add your calculators!</h2>
                <div>
                    <h3>Add each calculator to your HTML:</h3>
                        <p>{perCalcHtmlRender}</p>
                        <p>{dolCalcHtmlRender}</p>
                </div>
                <div>
                    Add this CSS to your website's stylesheet:
                </div>
                <div>
                    Add this JavaScript to the head of your webpage:
                </div>
            </div>
        )
    }
}

export default CodeOutput;
