import React, { Component } from 'react';
import Navigation from './components/Navigation/Navigation';
import Header from './components/Header/Header';
import FieldGenerator from './components/FieldGenerator/FieldGenerator';
import PercentWizard from "./components/FieldGenerator/PercentWizard";
import DollarWizard from "./components/FieldGenerator/DollarWizard";
import "tachyons";
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
    input: '',
    addPercentField: [],
    addDollarField: [],
    route: 'signin',
    isSignedIn: false,
    user: {
        id: '',
        name: '',
        email: '',
        entries: 0,
        joined: ''
    }
    }
  }

  addPercentField = () => {
    const percentArrayLength=this.state.addPercentField.length;
    if (percentArrayLength === 0) {
    this.push(`percentWizard`)
    } else {
        this.push(`percentWizard` + {percentArrayLength})
    }
}

  addDollarField = () => {
    const dollarArrayLength=this.state.addDollarField.length;
    if (DollarArrayLength === 0) {
    this.push(`percentWizard`)
    } else {
        this.push(`percentWizard` + {percentArrayLength})
    }
}

  render() {
    return (
      <div>
        <Navigation />
        <Header />
        <FieldGenerator />
      </div>
    )
  }  
}


export default App;
