import React from "react";

class SavingsResult extends React.Component {

    savings() {
        if (this.props.businessRate.includes('dollar')) {
            return ('$' + parseFloat(parseFloat(this.props.customerRate) - parseFloat(this.props.businessRate)));
        } else {
            return (parseFloat(this.props.customerRate) - parseFloat(this.props.businessRate) + '%');
        }
    }

    render() {
        return (
            <div>
                <p>We can save you up to {this.savings}!</p>
            </div>
        )
    }
}

export default SavingsResult;
