import React from "react";
import Result from "./Result.js"

class PercentCalculator extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            results: ""
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
    }
    render() {
        // const calculateSavings = () => {
        //     let thisKey = item
        //     let linkedWiz = {document.getElementById.div}
        //     let businessRate = document.getElementById;
        //     let b = setPercent;
        //     if (a - b > 0) {
        //         return a - b;
        //     }
        //     else {
        //         return `Your current rate can't be beat!`
        //     }
        // }

        const percentWizardRender = this.props.percentWizardCount.map((key, count) =>
            <div id={key} key={count}>
                <Result
                onPercentFormSubmit={this.props.onPercentFormSubmit}
                wizardId={count}
                />
            </div>
        )

        const partnerWizard = this.props.percentCalculator[this.props.calcId];
        const promptText = partnerWizard.prompt;

        return (
            <div className="ba br2 mb3">
                <div>
                    <h3>{promptText}</h3>
                        <p><input 
                            className="br2" 
                            type="text"
                            placeholder="4"/>
                            %</p>
                        <button
                            className="f6 link dim br3 ph3 pv2 mb2 dib white bg-dark-blue"
                        >See your savings!</button>
                </div>
            </div>
        )
    }
}

export default PercentCalculator
