import React from "react";

class SavingsResult extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            customerRate: this.props.customerRate,
        }
    }

    render() {
        let yourRate = parseInt(this.props.yourRate);
        let customerRate = parseInt(this.state.customerRate);
        let savings = parseInt(customerRate - yourRate);
        return (
            <div>
                <p>We can save you up to {savings}%!</p>
            </div>
        )
    }

    componentDidMount() {
        this.setState({customerRate: this.props.customerRate})
    }
}

export default SavingsResult;
