import React from "react";

class PercentCalculatorJavaScript extends React.Component {

    render() {

        const perCalcRate = this.props.percentCalculator[this.props.calcId].rate;
        const calcId = this.props.calcId + 1;

        return (
            <div>
                <h3 className="tc f5 f4-l fw3 lh-copy dark-gray">Percent Calculator {calcId} JavaScript</h3>
                    <div>
                        <p className="f7 f6-ns fw3 ph2 dark-gray">
                        {
                            `let perCalc${calcId}Rate = ${perCalcRate};
                            let perCalc${calcId}Submitted = false;
                            let perCalcCustomerRate${calcId} = undefined;
                            let perCalcSavings${calcId} = undefined;

                            document.getElementById("PercentCalculator1").addEventListener('submit', rateSubmit);

                            function calculateSavings() {
                                if (perCalc${calcId}Submitted = true) {
                                    let difference = (parseFloat(customerRate${calcId}) - parseFloat(perCalc${calcId}Rate)).toFixed(2)
                                    if (difference > 0) {
                                        perCalcSavings${calcId} = ``We can save you up to ${difference}%!``;
                                    } else {
                                        perCalcSavings${calcId} = "We can match your current rate!"
                                    }
                                }
                                showResult();
                            }

                            function showResult() {
                                document.getElementById("resultField").innerHTML = perCalcSavings${calcId};
                            }

                            function rateSubmit(e) {
                                e.preventDefault();
                                perCalc${calcId}Submitted = true;
                                let submittedRate = document.getElementById("perCalcInput${calcId}").value;
                                customerRate${calcId} = submittedRate;
                                calculateSavings();
                            }`
                        }
                        </p>
                    </div>
            </div>
        )
    }
}

export default PercentCalculatorJavaScript;
