import React from "react";

class PercentCalculator extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            customerRate: "",
        }
        this.handleChange = this.handleChange.bind(this);
        this.resultRender = this.resultRender.bind(this);
    }

    handleChange (e) {
        this.setState({ [e.target.name]: e.target.value });
        e.preventDefault();
        console.log(this.state.customerRate)
    }

//need to get Result.js reading this component's state and rendering correctly

    resultRender() {
    if (this.state.customerRate === undefined) {
        return false
    } else {
        let yourRate = this.props.percentCalculator[this.props.calcId].rate;
        let customerRate = this.state.customerRate;      
        if (yourRate - customerRate > 0) {
            let savings = yourRate - customerRate;
            return (
                <div>
                <p>We can save you up to {savings}</p>
                </div>
            )
        }
        else {
            return (
                <div>
                <p>We can match your current rate!</p>
                </div>
            )
        }
    }
}

    render() {
        const partnerWizard = this.props.percentCalculator[this.props.calcId];
        const promptText = partnerWizard.prompt;
        return (
            <div className="ba br2 mb3">
                <form onSubmit={this.handleChange}>
                    <h2>{promptText}</h2>
                        <p><input
                            onChange={this.handleChange} 
                            className="br2" 
                            type="text"
                            name="customerRate"
                            placeholder="4"/>
                            %</p>
                        <button
                            className="f6 link dim br3 ph3 pv2 mb2 dib white bg-dark-blue"
                        >See your savings!</button>
                </form>
                <div>
                    {this.resultRender}
                </div>
            </div>
        )
    }
}

export default PercentCalculator;
